-- Prints with type and year
CREATE VIEW vAusgabeTypDatierung AS
SELECT Ausgabe.idAusgabe, 
Ausgabe.Nachweis, 
IIF(Quellenart.Bezeichnung='Tabulatur','Tabulatur', IIF(Quellenart.Bezeichnung='Musiktheorie','Musiktheorie','Ausgabe')) AS Typ,
Zeit.Mittel AS Datierung, Zeit.Von AS DatierungVon, Zeit.Bis AS DatierungBis 
FROM Ausgabe, Quellenart
LEFT JOIN Zeit ON Zeit.idZeit = Ausgabe.Datierung
WHERE Quellenart.idQuellenart = Ausgabe.Art
AND Ausgabe.genutzt = 1
ORDER BY Ausgabe.idAusgabe;


-- Number of concordances per type and year
CREATE VIEW vDatierungKonkordanzenAnzahl AS
SELECT Datierung, 
	COUNT(idKonkordanz) AS Konk_total,
	COUNT(IIF(QuellenTyp='Handschrift',idKonkordanz, NULL)) AS Konk_Hs,
	COUNT(IIF(QuellenTyp='Kernkorpus',idKonkordanz, NULL)) AS Konk_kern,
	COUNT(IIF(QuellenTyp='Ausgabe',idKonkordanz, NULL)) AS Konk_Ausgaben,
	COUNT(IIF(QuellenTyp='Tabulatur',idKonkordanz, NULL)) AS Konk_Tabulatur,
	COUNT(IIF(QuellenTyp='Musiktheorie',idKonkordanz, NULL)) AS Konk_Theorie
FROM vRepertoire
GROUP BY Datierung;


-- Manuscripts with type and date
CREATE VIEW vHandschriftTypDatierung AS
SELECT Handschrift.idHandschrift, 
Handschrift.CensusCat, 
Zeit.Mittel AS Datierung,
Zeit.Von AS DatierungVon,
Zeit.Bis AS DatierungBis,
CASE
	WHEN Quellenart.Bezeichnung='Tabulatur' THEN 'Tabulatur'
	WHEN Quellenart.Bezeichnung='Musiktheorie' THEN 'Musiktheorie'
	WHEN Handschrift.CensusCat IN ('VatS 14', 'VatS 23', 'VatS 35', 'VatS 41', 'VatS 49', 'VatS 49', 'VatS 51', 'VatS 63', 'VatS 64', 'VatS 197') THEN 'Kernkorpus'
	ELSE 'Handschrift'
END Typ
FROM Handschrift, Quellenart
LEFT JOIN Zeit ON Zeit.idZeit = Handschrift.Datierung
WHERE Quellenart.idQuellenart = Handschrift.Art 
ORDER BY idHandschrift;


-- Overview of used concordances with data instead of FKs
CREATE VIEW vRepertoire AS
SELECT Konkordanz.idKonkordanz, Konkordanz.WerkID AS Werk, Werk.Titel, Komponist.Bezeichnung,
IIF(Konkordanz.Handschrift IS NOT NULL,
	(SELECT Typ FROM vHandschriftTypDatierung WHERE Konkordanz.Handschrift = vHandschriftTypDatierung.idHandschrift),
	(SELECT Typ FROM vAusgabeTypDatierung WHERE Konkordanz.Ausgabe = vAusgabeTypDatierung.idAusgabe))
	AS QuellenTyp,
IIF(Konkordanz.Handschrift IS NOT NULL, 
	(SELECT CensusCat FROM vHandschriftTypDatierung WHERE Konkordanz.Handschrift = vHandschriftTypDatierung.idHandschrift), 
	(SELECT Nachweis FROM vAusgabeTypDatierung WHERE Konkordanz.Ausgabe = vAusgabeTypDatierung.idAusgabe))
	AS Quelle,
Konkordanz.Quellen_Index, 
CASE 
	WHEN Konkordanz.Datierung IS NOT NULL THEN (SELECT Mittel FROM Zeit WHERE Zeit.idZeit = Konkordanz.Datierung)
	WHEN EXISTS(SELECT Datierung FROM vHandschriftTypDatierung WHERE vHandschriftTypDatierung.idHandschrift = Konkordanz.Handschrift) 
		THEN (SELECT Datierung FROM vHandschriftTypDatierung WHERE vHandschriftTypDatierung.idHandschrift = Konkordanz.Handschrift)
	WHEN EXISTS(SELECT Datierung FROM vAusgabeTypDatierung WHERE vAusgabeTypDatierung.idAusgabe = Konkordanz.Ausgabe)
		THEN (SELECT Datierung FROM vAusgabeTypDatierung WHERE vAusgabeTypDatierung.idAusgabe = Konkordanz.Ausgabe)
	ELSE NULL
 END Datierung,
Konkordanz.Stimmzahl, Konkordanz.Kyrie, Konkordanz.Gloria, Konkordanz.Credo, Konkordanz.Sanctus, Konkordanz.Agnus
FROM Konkordanz, Werk, Komponist
WHERE Konkordanz.genutzt = 1
AND Werk.idWerk = Konkordanz.WerkID
AND Komponist.idKomponist = Werk.Komponist
ORDER BY idKonkordanz;

-- Works with concordances per type
CREATE VIEW vWerkKonkordanzenAnzahl AS
SELECT Werk.idWerk, Werk.Titel, Komponist.Bezeichnung, 
(SELECT COUNT(vRepertoire.idKonkordanz) FROM vRepertoire WHERE vRepertoire.Werk = Werk.idWerk) AS Konk_total,
(SELECT COUNT(vRepertoire.idKonkordanz) FROM vRepertoire WHERE vRepertoire.Werk = Werk.idWerk AND vRepertoire.QuellenTyp = 'Kernkorpus') AS Konk_kern,
(SELECT COUNT(vRepertoire.idKonkordanz) FROM vRepertoire WHERE vRepertoire.Werk = Werk.idWerk AND vRepertoire.QuellenTyp = 'Handschrift') AS Konk_Hs,
(SELECT COUNT(vRepertoire.idKonkordanz) FROM vRepertoire WHERE vRepertoire.Werk = Werk.idWerk AND vRepertoire.QuellenTyp = 'Ausgabe') AS Konk_Ausgaben,
(SELECT COUNT(vRepertoire.idKonkordanz) FROM vRepertoire WHERE vRepertoire.Werk = Werk.idWerk AND vRepertoire.QuellenTyp = 'Tabulatur') AS Konk_Tabulatur,
(SELECT COUNT(vRepertoire.idKonkordanz) FROM vRepertoire WHERE vRepertoire.Werk = Werk.idWerk AND vRepertoire.QuellenTyp = 'Musiktheorie') AS Konk_Theorie
FROM Werk, Komponist
WHERE Komponist.idKomponist = Werk.Komponist
ORDER BY Werk.idWerk ASC

