---
-- Create node list view containing Werk, Ausgabe & Handschrift (only used ones)
-- Bi-mode network of works and sources
-- id, label, type, sourcetype
---

CREATE VIEW networkNodes AS
SELECT Handschrift.idHandschrift as id, 
Handschrift.CensusCat as label,
"source" as type,
CASE
	WHEN Quellenart.Bezeichnung='Tabulatur' THEN 'Tabulatur'
	WHEN Quellenart.Bezeichnung='Musiktheorie' THEN 'Musiktheorie'
	WHEN Handschrift.CensusCat IN ('VatS 14', 'VatS 23', 'VatS 35', 'VatS 41', 'VatS 49', 'VatS 49', 'VatS 51', 'VatS 63', 'VatS 64', 'VatS 197') THEN 'Kernkorpus'
	ELSE 'Handschrift'
END sourceType
FROM Handschrift, Quellenart
WHERE Quellenart.idQuellenart = Handschrift.Art 
AND Handschrift.genutzt = 1
UNION
SELECT idAusgabe as id, Nachweis as label, "source" as type,
IIF(Quellenart.Bezeichnung='Tabulatur','Tabulatur', IIF(Quellenart.Bezeichnung='Musiktheorie','Musiktheorie','Ausgabe')) AS sourceType
FROM Ausgabe, Quellenart
WHERE Quellenart.idQuellenart = Ausgabe.Art
AND Ausgabe.genutzt = 1
UNION
SELECT idWerk as id, Werk.Titel || " - " || Komponist.Bezeichnung as label, "work" as type, " " as sourceType
FROM Werk, Komponist
WHERE Komponist.idKomponist = Werk.Komponist
AND Werk.genutzt = 1;


---
-- Create edge list view from Konkordanz (only used ones)
-- Only take one edge per tuple to normalise sources containing multiple parts of a mass (we're only interested in the connection)
-- (group by make edges unique)
---

CREATE VIEW networkLines AS
SELECT WerkID as Source, coalesce(Handschrift,Ausgabe) as Target
FROM Konkordanz
WHERE Konkordanz.genutzt = 1
GROUP BY Source, Target;